import React from "react";
import {
  ListGroup,
  Table,
  Form,
  InputGroup,
  Button,
  FormControl
} from "react-bootstrap";
import Notification from "./Notification";

const JobsByKeyWordsComponent = props => {
  console.log("JobsByKeyWords Components Jobs", props.jobs);
  return (
    <>
      {props.message && <Notification message={props.message} />}
      <Form onSubmit={props.submitHandler}>
        <Form.Group>
          <Form.Label>words:</Form.Label>
          <Form.Control
            name="words"
            placeholder="e.g. software, company, java, director"
            {...props.words}
          />
          <Form.Label>area:</Form.Label>
          <Form.Control
            name="area"
            placeholder="e.g. Helsinki"
            {...props.area}
          />
          <Form.Label>time range:</Form.Label>
          <Form.Control
            name="area"
            placeholder="e.g. 2018-11"
            {...props.time_range_start}
          />
          <br />
          <Button variant="primary" type="submit">
            Search for skills
          </Button>
        </Form.Group>
      </Form>
      {/*        {props.message && <Notification message={props.message} />}
      <br />
      <h1>Words to skills</h1>
      <Form onSubmit={props.submitHandler}>
        <InputGroup>
          <FormControl
            {...props.textInput}
            required
            placeholder="Insert the text from your CV and convert it into words"
            as="textarea"
            aria-label="Search for skills"
          />
          <InputGroup.Append>
            <Button type="submit" variant="outline-secondary">
              Search for skills
            </Button>
          </InputGroup.Append>
        </InputGroup>
      </Form>{" "} */}

      <br />
      {!props.jobs ? (
        <>
          <h4>Skills are displayed here</h4>
          <ListGroup>
            <ListGroup.Item>Cras justo odio</ListGroup.Item>
            <ListGroup.Item>
              <span style={{ fontSize: "24px" }}>
                Java Kehittäjä, CGI Central Goverment
              </span>
              <span> (matching skills: software, java)</span>
              <p>Helsinki - 2019-02-05</p>
            </ListGroup.Item>

            <ListGroup.Item>Morbi leo risus</ListGroup.Item>
            <ListGroup.Item>Porta ac consectetur ac</ListGroup.Item>
            <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
          </ListGroup>
        </>
      ) : (
        <ListGroup>
          {props.jobs.map(job => {
            return (
              <>
                <ListGroup.Item>
                  <a style={{ cursor: "pointer" }}>
                    <span style={{ fontSize: "24px" }}>{job.id}</span>
                    <span>
                      {job.skills_that_match
                        ? `(skills that match: ${job.skills_that_match})`
                        : null}
                      {/*                     {job.skills_that_match.map(skill => ` ${skill}`)})
                       */}
                    </span>
                    <p>{job.time}</p>
                  </a>
                </ListGroup.Item>
              </>
            );
          })}
        </ListGroup>
        /*         <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Skills</th>
            </tr>
          </thead>
          <tbody>
            {props.skills.map((skill, index) => {
              return (
                <>
                  <tr>
                    <td>{index}</td>
                    <td>{skill}</td>
                  </tr>
                </>
              );
            })}
          </tbody>
        </Table> */
      )}
    </>
  );
};

export default JobsByKeyWordsComponent;
